fn count_set_bits_in_range(start: u32, end: u32) -> u32 {
    // Counts the total number of set bits in the binary representations of all integers within the range [start, end] inclusive.
    // 
    // Parameters:
    // start - The starting number of the range (inclusive)
    // end - The ending number of the range (inclusive)
    //
    // Returns:
    // The total count of set bits in all numbers from start to end

    // Validate input values
    if start > end {
        panic!("start should be less than or equal to end");
    }

    // Count set bits in the given range
    (start..=end).map(|number| number.count_ones()).sum()
}

