fn minimize_absolute_differences(nums: &mut Vec<i32>) -> Vec<i32> {
    // Rearranges the array such that the sum of absolute differences between
    // consecutive elements is minimized by sorting and interleaving from both ends.

    nums.sort();
    let n = nums.len();
    let mut result = Vec::with_capacity(n);

    for i in 0..n / 2 {
        result.push(nums[n - 1 - i]);
        result.push(nums[i]);
    }

    if n % 2 != 0 {
        result.push(nums[n / 2]);
    }

    // Handle even lengths by potentially reversing
    if result.len() == n {
        result
    } else {
        result.into_iter().rev().collect()
    }
}

assertions:
#[test]
fn test_minimize_absolute_differences() {
    let mut input1 = vec![1, 2, 3, 4];
    assert_eq!(minimize_absolute_differences(&mut input1), vec![4, 1, 3, 2]);

    let mut input2 = vec![5, 2, 7, 1, 3];
    assert_eq!(minimize_absolute_differences(&mut input2), vec![7, 1, 5, 2, 3]);

    let mut input3 = vec![10, 20];
    assert_eq!(minimize_absolute_differences(&mut input3), vec![20, 10]);

    let mut input4 = vec![8];
    assert_eq!(minimize_absolute_differences(&mut input4), vec![8]);

    let mut input5 = vec![4, 6, 2, 9, 5];
    assert_eq!(minimize_absolute_differences(&mut input5), vec![9, 2, 6, 4, 5]);
}

