fn format_license_key(key: &str, k: usize) -> String {
    // Formats the input license key string such that every k characters have a dash in between.
    // 
    // Parameters:
    // key - The input license key string composed of alphanumeric characters and dashes.
    // k - The segment length.
    //
    // Returns:
    // The reformatted license key string.

    // Remove existing dashes and convert all characters to uppercase
    let cleaned_key: String = key.chars()
        .filter(|&c| c != '-')
        .map(|c| c.to_ascii_uppercase())
        .collect();

    let length = cleaned_key.len();
    let mut result = Vec::new();

    // Process the string in reverse to group them in chunks of k
    for (i, ch) in cleaned_key.chars().rev().enumerate() {
        if i > 0 && i % k == 0 {
            result.push('-');
        }
        result.push(ch);
    }

    // Reverse again to get the correct order and collect into String
    result.iter().rev().collect()
}

assertions:
#[test]
fn test_format_license_key() {
    assert_eq!(format_license_key("5F3Z-2e-9-w", 4), "5F3Z-2E9W");
    assert_eq!(format_license_key("2-5g-3-J", 2), "2-5G-3J");
    assert_eq!(format_license_key("2-4A0r7-4k", 3), "24-A0R-74K");
    assert_eq!(format_license_key("a-b-c-d-e-f", 1), "A-B-C-D-E-F");
    assert_eq!(format_license_key("---", 2), "");
    assert_eq!(format_license_key("", 4), "");
}

