// Struct to represent the array query processor
struct ArrayQueryProcessor {
    n: usize,
    arr: Vec<i32>,
}

impl ArrayQueryProcessor {
    // Create a new ArrayQueryProcessor with given size and array
    fn new(n: usize, arr: Vec<i32>) -> Self {
        ArrayQueryProcessor { n, arr }
    }

    // Execute a single query on the array
    fn execute_query(&mut self, query: &[i32]) -> Option<i32> {
        match query[0] {
            1 => {
                // Update operation: set arr[p-1] = x
                let p = query[1] as usize;
                let x = query[2];
                self.arr[p - 1] = x;
                None
            }
            2 => {
                // Sum operation: return sum of elements from l-1 to r-1
                let l = query[1] as usize;
                let r = query[2] as usize;
                Some(self.arr[(l - 1)..r].iter().sum())
            }
            _ => None,
        }
    }
}

// Process multiple queries on an array
fn process_queries(n: usize, arr: Vec<i32>, queries: Vec<Vec<i32>>) -> Vec<i32> {
    let mut processor = ArrayQueryProcessor::new(n, arr);
    let mut results = Vec::new();

    for query in queries {
        if let Some(result) = processor.execute_query(&query) {
            results.push(result);
        }
    }

    results
}

