fn test_array_query_processor() {
    // Test case 1: Basic operations
    let n1 = 5;
    let arr1 = vec![1, 2, 3, 4, 5];
    let queries1 = vec![
        vec![2, 2, 4],  // Sum elements 2-4 (indices 1-3) = 2+3+4 = 9
        vec![1, 3, 10], // Update element 3 to 10
        vec![2, 2, 4],  // Sum elements 2-4 (indices 1-3) = 2+10+4 = 16
    ];
    assert_eq!(process_queries(n1, arr1, queries1), vec![9, 16]);

    // Test case 2: Edge cases
    let n2 = 3;
    let arr2 = vec![0, 0, 0];
    let queries2 = vec![
        vec![1, 1, 5],  // Update element 1 to 5
        vec![1, 2, 7],  // Update element 2 to 7
        vec![2, 1, 3],  // Sum all elements = 5+7+0 = 12
        vec![2, 2, 2],  // Sum single element = 7
    ];
    assert_eq!(process_queries(n2, arr2, queries2), vec![12, 7]);

    // Test case 3: Single element array
    let n3 = 1;
    let arr3 = vec![100];
    let queries3 = vec![
        vec![2, 1, 1],  // Sum single element = 100
        vec![1, 1, 50], // Update element 1 to 50
        vec![2, 1, 1],  // Sum single element = 50
    ];
    assert_eq!(process_queries(n3, arr3, queries3), vec![100, 50]);
}

