use std::collections::HashMap;

/// Processes warehouse operations and returns query results
///
/// # Arguments
/// * `n` - Number of item types (unused in this implementation)
/// * `m` - Number of operations
/// * `item_names` - List of item names to initialize inventory
/// * `operations` - List of operations to process (ADD, REMOVE, QUERY)
///
/// # Returns
/// Vector of strings containing QUERY results in "item count" format
fn warehouse_operations(n: usize, m: usize, item_names: Vec<&str>, operations: Vec<&str>) -> Vec<String> {
    // Initialize inventory with all items set to count 0
    let mut inventory: HashMap<&str, i32> = item_names.iter().map(|&item| (item, 0)).collect();
    let mut results = Vec::new();

    // Process each operation
    for operation in operations {
        let parts: Vec<&str> = operation.split_whitespace().collect();

        match parts[0] {
            "ADD" => {
                let item = parts[1];
                let count: i32 = parts[2].parse().unwrap();
                *inventory.entry(item).or_insert(0) += count;
            },
            "REMOVE" => {
                let item = parts[1];
                let count: i32 = parts[2].parse().unwrap();
                *inventory.entry(item).or_insert(0) -= count;
            },
            "QUERY" => {
                let item = parts[1];
                if let Some(&count) = inventory.get(item) {
                    results.push(format!("{} {}", item, count));
                }
            },
            _ => panic!("Unknown operation"),
        }
    }

    results
}

assertions:
#[test]
fn test_warehouse_operations() {
    // Test case 1: Basic operations
    let n1 = 3;
    let m1 = 5;
    let items1 = vec!["apple", "banana", "orange"];
    let ops1 = vec![
        "ADD apple 10",
        "ADD banana 5",
        "QUERY apple",
        "REMOVE apple 3",
        "QUERY apple",
    ];
    assert_eq!(
        warehouse_operations(n1, m1, items1, ops1),
        vec!["apple 10", "apple 7"]
    );

    // Test case 2: Item not in initial inventory
    let n2 = 2;
    let m2 = 4;
    let items2 = vec!["book", "pen"];
    let ops2 = vec![
        "ADD book 15",
        "ADD pencil 20",
        "QUERY book",
        "QUERY pencil",
    ];
    assert_eq!(
        warehouse_operations(n2, m2, items2, ops2),
        vec!["book 15", "pencil 20"]
    );

    // Test case 3: Remove operation
    let n3 = 1;
    let m3 = 3;
    let items3 = vec!["chair"];
    let ops3 = vec![
        "ADD chair 8",
        "REMOVE chair 3",
        "QUERY chair",
    ];
    assert_eq!(
        warehouse_operations(n3, m3, items3, ops3),
        vec!["chair 5"]
    );
}

