use std::collections::VecDeque;

// Function to count tree clusters in a forest represented as a 2D grid
// Uses Depth-First Search (DFS) to mark connected trees as visited
fn count_tree_clusters(forest: &mut Vec<Vec<char>>) -> usize {
    let rows = forest.len();
    if rows == 0 {
        return 0;
    }
    let cols = forest[0].len();
    let mut cluster_count = 0;

    // Iterate through each cell in the forest
    for i in 0..rows {
        for j in 0..cols {
            if forest[i][j] == 'T' {
                cluster_count += 1;
                dfs(i, j, rows, cols, forest);
            }
        }
    }

    cluster_count
}

// Helper DFS function to mark all connected trees as visited
fn dfs(x: usize, y: usize, rows: usize, cols: usize, forest: &mut Vec<Vec<char>>) {
    let mut stack = VecDeque::new();
    stack.push_back((x, y));

    while let Some((i, j)) = stack.pop_back() {
        if forest[i][j] != 'T' {
            continue;
        }

        forest[i][j] = '.'; // Mark as visited

        // Check and add adjacent cells to the stack
        if i > 0 && forest[i - 1][j] == 'T' {
            stack.push_back((i - 1, j));
        }
        if i < rows - 1 && forest[i + 1][j] == 'T' {
            stack.push_back((i + 1, j));
        }
        if j > 0 && forest[i][j - 1] == 'T' {
            stack.push_back((i, j - 1));
        }
        if j < cols - 1 && forest[i][j + 1] == 'T' {
            stack.push_back((i, j + 1));
        }
    }
}

assertions:
#[test]
fn test_count_tree_clusters() {
    // Test case 1: Single tree
    let mut forest1 = vec![
        vec!['.', '.', '.'],
        vec!['.', 'T', '.'],
        vec!['.', '.', '.'],
    ];
    assert_eq!(count_tree_clusters(&mut forest1), 1);

    // Test case 2: Two separate clusters
    let mut forest2 = vec![
        vec!['T', '.', 'T'],
        vec!['.', '.', '.'],
        vec!['T', '.', 'T'],
    ];
    assert_eq!(count_tree_clusters(&mut forest2), 4);

    // Test case 3: One large connected cluster
    let mut forest3 = vec![
        vec!['T', 'T', '.'],
        vec!['T', '.', 'T'],
        vec!['.', 'T', 'T'],
    ];
    assert_eq!(count_tree_clusters(&mut forest3), 1);

    // Test case 4: Empty forest
    let mut forest4 = vec![
        vec!['.', '.'],
        vec!['.', '.'],
    ];
    assert_eq!(count_tree_clusters(&mut forest4), 0);

    // Test case 5: All trees but no connections
    let mut forest5 = vec![
        vec!['T', '.', 'T'],
        vec!['T', '.', 'T'],
        vec!['T', '.', 'T'],
    ];
    assert_eq!(count_tree_clusters(&mut forest5), 6);
}

