/// Finds an element that appears more than n/3 times in the array.
/// Returns the element if found, otherwise returns -1.
/// Uses Moore's Voting Algorithm to efficiently find potential candidates.
fn find_element_appearing_more_than_n_by_3(nums: &[i32]) -> i32 {
    if nums.is_empty() {
        return -1;
    }

    let n = nums.len();
    let (mut candidate1, mut candidate2) = (None, None);
    let (mut count1, mut count2) = (0, 0);

    // First pass: Find two potential candidates
    for &num in nums {
        if candidate1 == Some(num) {
            count1 += 1;
        } else if candidate2 == Some(num) {
            count2 += 1;
        } else if count1 == 0 {
            candidate1 = Some(num);
            count1 = 1;
        } else if count2 == 0 {
            candidate2 = Some(num);
            count2 = 1;
        } else {
            count1 -= 1;
            count2 -= 1;
        }
    }

    // Second pass: Verify the candidates
    let (mut count1, mut count2) = (0, 0);
    for &num in nums {
        if Some(num) == candidate1 {
            count1 += 1;
        } else if Some(num) == candidate2 {
            count2 += 1;
        }
    }

    if count1 > n / 3 {
        return candidate1.unwrap();
    }
    if count2 > n / 3 {
        return candidate2.unwrap();
    }

    -1
}

