    assert_eq!(find_element_appearing_more_than_n_by_3(&[1, 2, 3, 4, 5]), -1);
    assert_eq!(find_element_appearing_more_than_n_by_3(&[2, 2, 1, 3, 1, 2, 2]), 2);
    assert_eq!(find_element_appearing_more_than_n_by_3(&[]), -1);
    assert_eq!(find_element_appearing_more_than_n_by_3(&[1, 1, 1, 3, 3, 3, 3]), 3);
}

