fn babylonian_sqrt(n: f64, p: i32) -> Result<f64, String> {
    // Returns the square root of a number 'n' with precision 'p' using the Babylonian method.
    //
    // Parameters:
    // n - The number to find the square root of
    // p - The precision (number of decimal places)
    //
    // Returns:
    // Result<f64, String> - The square root of 'n' rounded to 'p' decimal places,
    // or an error message if inputs are invalid

    if n < 0.0 {
        return Err("Cannot compute the square root of a negative number".to_string());
    }
    if p < 0 {
        return Err("Precision cannot be negative".to_string());
    }

    let mut x = n;
    let mut y = 1.0;
    let e = 0.1_f64.powi(p + 1);

    while (x - y).abs() > e {
        x = (x + y) / 2.0;
        y = n / x;
    }

    let scale = 10_f64.powi(p);
    Ok((x * scale).round() / scale)
}

assertions:
#[test]
fn test_babylonian_sqrt() {
    // Test cases with expected results
    assert_eq!(babylonian_sqrt(16.0, 2), Ok(4.0));
    assert_eq!(babylonian_sqrt(25.0, 3), Ok(5.0));
    assert_eq!(babylonian_sqrt(2.0, 5), Ok(1.41421));
    assert_eq!(
        babylonian_sqrt(-4.0, 1),
        Err("Cannot compute the square root of a negative number".to_string())
    );
    assert_eq!(
        babylonian_sqrt(10.0, -2),
        Err("Precision cannot be negative".to_string())
    );
    assert_eq!(babylonian_sqrt(0.0, 1), Ok(0.0));
}

