use std::iter::Peekable;
use std::str::Chars;

/// Reverses the words in the given string while preserving whitespace.
///
/// # Arguments
/// * `s` - A string slice containing the input text.
///
/// # Returns
/// A String with words in reverse order, preserving original whitespace.
fn reverse_words(s: &str) -> String {
    if s.is_empty() || s.chars().all(|c| c.is_whitespace()) {
        return s.to_string();
    }

    // Split words and reverse their order
    let words: Vec<&str> = s.split_whitespace().collect();
    let reversed_words: Vec<&str> = words.into_iter().rev().collect();
    let mut word_iter = reversed_words.into_iter().peekable();

    let mut result = String::new();
    let mut char_iter = s.chars().peekable();

    // Rebuild the string with original whitespace but reversed words
    while let Some(c) = char_iter.next() {
        if c.is_whitespace() {
            result.push(c);
        } else {
            // Push the next reversed word
            if let Some(word) = word_iter.next() {
                result.push_str(word);
                // Skip characters until next whitespace
                while char_iter.peek().map_or(false, |&c| !c.is_whitespace()) {
                    char_iter.next();
                }
            }
        }
    }

    result
}

assertions:
#[test]
fn test_reverse_words() {
    assert_eq!(reverse_words("hello world"), "world hello");
    assert_eq!(reverse_words("  hello   world  "), "  world   hello  ");
    assert_eq!(reverse_words("single"), "single");
    assert_eq!(reverse_words("  "), "  ");
    assert_eq!(reverse_words(""), "");
    assert_eq!(reverse_words("a b c d e"), "e d c b a");
    assert_eq!(reverse_words(" leading space"), "space leading");
    assert_eq!(reverse_words("trailing space "), "space trailing ");
}

