fn is_sorted(books: &[String]) -> &'static str {
    // Checks if a slice of book titles is sorted in alphabetical order.
    // Returns "YES" if sorted, "NO" otherwise.
    for i in 0..books.len() - 1 {
        if books[i] > books[i + 1] {
            return "NO";
        }
    }
    "YES"
}

