fn subarray_beauty(n: usize, a: &[i32], queries: &[(usize, usize)]) -> Vec<i32> {
    // Calculate the beauty for each query
    // Beauty is defined as the difference between max and min elements in the subarray
    queries.iter().map(|&(l, r)| {
        let subarray = &a[l-1..r];
        let max_element = *subarray.iter().max().unwrap();
        let min_element = *subarray.iter().min().unwrap();
        max_element - min_element
    }).collect()
}

assertions:
#[test]
fn test_subarray_beauty() {
    // Test case 1: Simple case with 5 elements and 2 queries
    let n1 = 5;
    let a1 = vec![1, 3, 5, 2, 4];
    let queries1 = vec![(1, 3), (2, 5)];
    assert_eq!(subarray_beauty(n1, &a1, &queries1), vec![4, 3]);

    // Test case 2: All elements same
    let n2 = 4;
    let a2 = vec![7, 7, 7, 7];
    let queries2 = vec![(1, 4), (2, 3), (1, 1)];
    assert_eq!(subarray_beauty(n2, &a2, &queries2), vec![0, 0, 0]);

    // Test case 3: Single element array
    let n3 = 1;
    let a3 = vec![10];
    let queries3 = vec![(1, 1)];
    assert_eq!(subarray_beauty(n3, &a3, &queries3), vec![0]);

    // Test case 4: Large range with negative numbers
    let n4 = 6;
    let a4 = vec![-5, 0, 3, -2, 8, -1];
    let queries4 = vec![(1, 6), (2, 4), (3, 5)];
    assert_eq!(subarray_beauty(n4, &a4, &queries4), vec![13, 5, 10]);
}

