fn longest_alternating_subsequence(nums: &[i32]) -> i32 {
    // Calculate the length of the longest alternating subsequence.
    // An alternating subsequence is one where the elements alternate between increasing and decreasing.
    // The function uses dynamic programming to track the longest increasing and decreasing subsequences ending at each position.

    if nums.is_empty() {
        return 0;
    }

    let n = nums.len();
    let mut inc = 1;  // length of longest alternating subsequence ending at current position with increasing trend
    let mut dec = 1;  // length of longest alternating subsequence ending at current position with decreasing trend

    for i in 1..n {
        if nums[i] > nums[i - 1] {
            // Current element is greater than previous, so we can extend the decreasing subsequence
            inc = dec + 1;
        } else if nums[i] < nums[i - 1] {
            // Current element is smaller than previous, so we can extend the increasing subsequence
            dec = inc + 1;
        }
        // If equal, we don't change either inc or dec
    }

    std::cmp::max(inc, dec)
}

assertions:
#[test]
fn test_longest_alternating_subsequence() {
    assert_eq!(longest_alternating_subsequence(&[1, 5, 4, 7, 2, 9]), 5);
    assert_eq!(longest_alternating_subsequence(&[1, 2, 3, 4, 5]), 2);
    assert_eq!(longest_alternating_subsequence(&[5, 4, 3, 2, 1]), 2);
    assert_eq!(longest_alternating_subsequence(&[1, 1, 1, 1, 1]), 1);
    assert_eq!(longest_alternating_subsequence(&[]), 0);
    assert_eq!(longest_alternating_subsequence(&[1, 2, 1, 2, 1, 2]), 6);
}

