// Function to reverse words in a string that are longer than 4 characters
fn reverse_long_words(s: &str) -> String {
    // Split the input string into words
    let words: Vec<&str> = s.split_whitespace().collect();

    // Process each word: reverse if longer than 4 characters
    let modified_words: Vec<String> = words.iter().map(|&word| {
        if word.len() > 4 {
            word.chars().rev().collect()
        } else {
            word.to_string()
        }
    }).collect();

    // Join the modified words back into a single string
    modified_words.join(" ")
}

