fn add_ed(words: &[&str]) -> Vec<String> {
    // Processes a slice of string references and returns a new Vec<String>
    // where 'ed' is added to each word that doesn't already end with 'ed'.
    // Words ending with 'ed' remain unchanged.
    words.iter()
        .map(|&word| {
            if word.ends_with("ed") {
                word.to_string()
            } else {
                format!("{}ed", word)
            }
        })
        .collect()
}

