use std::collections::BinaryHeap;
use std::cmp::Reverse;

/// Calculates the minimum number of carousels needed to accommodate all intervals without overlap.
/// Uses a min-heap to track end times of carousels and assigns each interval to the earliest available carousel.
fn min_carousels(tests: &[(&[(i32, i32)], usize)]) -> Vec<usize> {
    let mut results = Vec::with_capacity(tests.len());

    for &(intervals, _) in tests {
        let mut sorted_intervals = intervals.to_vec();
        sorted_intervals.sort();

        // Min-heap to track the end times of carousels (using Reverse for min-heap behavior)
        let mut heap = BinaryHeap::new();

        for (start, end) in sorted_intervals {
            if let Some(&Reverse(earliest_end)) = heap.peek() {
                if earliest_end <= start {
                    heap.pop();
                }
            }
            heap.push(Reverse(end));
        }

        results.push(heap.len());
    }

    results
}

assertions:
#[test]
fn test_min_carousels() {
    let test_cases = vec![
        // Single interval
        (&[(1, 2)][..], 1),
        // Two non-overlapping intervals
        (&[(1, 2), (3, 4)][..], 2),
        // Two overlapping intervals
        (&[(1, 3), (2, 4)][..], 2),
        // Three intervals where two can share a carousel
        (&[(1, 3), (2, 4), (5, 6)][..], 2),
        // Multiple overlapping intervals
        (&[(1, 4), (2, 5), (3, 6), (7, 8)][..], 3),
    ];

    let results = min_carousels(&test_cases);

    assert_eq!(results, vec![1, 1, 2, 2, 3]);
}

