fn evaluate_knight_moves(board: &[Vec<i32>]) -> Vec<Vec<i32>> {
    // Possible moves for a knight in chess (dx, dy)
    let moves = [
        (2, 1), (2, -1), (-2, 1), (-2, -1),
        (1, 2), (1, -2), (-1, 2), (-1, -2)
    ];

    let n = board.len();
    let mut result = vec![vec![0; n]; n];

    for i in 0..n {
        for j in 0..n {
            if board[i][j] == 1 {  // There's a knight at this position
                let mut valid_moves = 0;
                for &(dx, dy) in &moves {
                    let ni = i as i32 + dx;
                    let nj = j as i32 + dy;
                    // Check if the move is within bounds
                    if ni >= 0 && ni < n as i32 && nj >= 0 && nj < n as i32 {
                        valid_moves += 1;
                    }
                }
                result[i][j] = valid_moves;
            }
        }
    }

    result
}

assertions:
