fn test_evaluate_knight_moves() {
    // Test case 1: Empty 3x3 board
    let board1 = vec![
        vec![0, 0, 0],
        vec![0, 0, 0],
        vec![0, 0, 0]
    ];
    assert_eq!(evaluate_knight_moves(&board1), vec![
        vec![0, 0, 0],
        vec![0, 0, 0],
        vec![0, 0, 0]
    ]);

    // Test case 2: Single knight in center of 3x3 board
    let board2 = vec![
        vec![0, 0, 0],
        vec![0, 1, 0],
        vec![0, 0, 0]
    ];
    assert_eq!(evaluate_knight_moves(&board2), vec![
        vec![0, 0, 0],
        vec![0, 8, 0],
        vec![0, 0, 0]
    ]);

    // Test case 3: Knights in corners of 4x4 board
    let board3 = vec![
        vec![1, 0, 0, 1],
        vec![0, 0, 0, 0],
        vec![0, 0, 0, 0],
        vec![1, 0, 0, 1]
    ];
    assert_eq!(evaluate_knight_moves(&board3), vec![
        vec![2, 0, 0, 2],
        vec![0, 0, 0, 0],
        vec![0, 0, 0, 0],
        vec![2, 0, 0, 2]
    ]);

    // Test case 4: Mixed positions on 5x5 board
    let board4 = vec![
        vec![1, 0, 0, 0, 1],
        vec![0, 0, 1, 0, 0],
        vec![0, 1, 0, 1, 0],
        vec![0, 0, 0, 0, 0],
        vec![1, 0, 0, 0, 0]
    ];
    assert_eq!(evaluate_knight_moves(&board4), vec![
        vec![2, 0, 0, 0, 1],
        vec![0, 0, 8, 0, 0],
        vec![0, 8, 0, 4, 0],
        vec![0, 0, 0, 0, 0],
        vec![2, 0, 0, 0, 0]
    ]);
