fn process_string(s: &str) -> String {
    // Processes a string by removing duplicate characters while preserving order
    // For each character in the input string:
    // - If it's already in the result, remove the existing occurrence
    // - If it's not in the result, add it to the end
    let mut new_string = Vec::new();

    for c in s.chars() {
        if let Some(pos) = new_string.iter().position(|&x| x == c) {
            new_string.remove(pos);
        } else {
            new_string.push(c);
        }
    }

    new_string.into_iter().collect()
}

