    assert_eq!(process_string("abcde"), "abcde");
    assert_eq!(process_string("aabbcc"), "");
    assert_eq!(process_string("hello world"), "he wrd");
    assert_eq!(process_string("testing"), "esting");
    assert_eq!(process_string(""), "");
    assert_eq!(process_string("a"), "a");
    assert_eq!(process_string("abacbc"), "");
}

