fn max_points_on_board(grid: &[Vec<i32>]) -> i32 {
    // Given a grid of n x n with point values, find the maximum points that can be collected in any path
    // from top-left to bottom-right corner moving only right or down.
    let n = grid.len();
    // Initialize a 2D dp array with the same dimensions as the grid
    let mut dp = vec![vec![0; n]; n];

    // Initialize the starting point
    dp[0][0] = grid[0][0];

    // Fill the first row and the first column
    for i in 1..n {
        dp[0][i] = dp[0][i - 1] + grid[0][i];
        dp[i][0] = dp[i - 1][0] + grid[i][0];
    }

    // Fill the rest of the dp array
    for i in 1..n {
        for j in 1..n {
            dp[i][j] = std::cmp::max(dp[i - 1][j], dp[i][j - 1]) + grid[i][j];
        }
    }

    // The bottom-right corner will have the maximum points collected
    dp[n - 1][n - 1]
}

assertions:
