fn test_max_points_on_board() {
    assert_eq!(
        max_points_on_board(&vec![
            vec![1, 2, 3],
            vec![4, 5, 6],
            vec![7, 8, 9],
        ]),
        29
    );
    assert_eq!(
        max_points_on_board(&vec![
            vec![1, 2],
            vec![3, 4],
        ]),
        8
    );
    assert_eq!(
        max_points_on_board(&vec![
            vec![1, 0, 0],
            vec![0, 1, 0],
            vec![0, 0, 1],
        ]),
        3
    );
}

