use std::collections::HashMap;

/// Calculates the cosine similarity between two text strings.
///
/// The function works by:
/// 1. Creating a vocabulary of all unique words from both texts
/// 2. Creating frequency vectors for each text based on the vocabulary
/// 3. Calculating the dot product and magnitudes of these vectors
/// 4. Computing the cosine similarity as dot product / (magnitude1 * magnitude2)
fn calculate_cosine_similarity(text1: &str, text2: &str) -> f64 {
    // Tokenize both texts into words (split on whitespace)
    let words1: Vec<&str> = text1.split_whitespace().collect();
    let words2: Vec<&str> = text2.split_whitespace().collect();

    // Create a vocabulary of all unique words
    let mut vocabulary = HashMap::new();
    let mut index = 0;

    for word in words1.iter().chain(words2.iter()) {
        vocabulary.entry(word.to_lowercase()).or_insert_with(|| {
            let current_index = index;
            index += 1;
            current_index
        });
    }

    // Create frequency vectors for each text
    let mut vec1 = vec![0; vocabulary.len()];
    let mut vec2 = vec![0; vocabulary.len()];

    for word in words1 {
        if let Some(&idx) = vocabulary.get(&word.to_lowercase()) {
            vec1[idx] += 1;
        }
    }

    for word in words2 {
        if let Some(&idx) = vocabulary.get(&word.to_lowercase()) {
            vec2[idx] += 1;
        }
    }

    // Calculate dot product
    let dot_product: i32 = vec1.iter().zip(vec2.iter()).map(|(&a, &b)| a * b).sum();

    // Calculate magnitudes
    let magnitude1 = (vec1.iter().map(|&x| x * x).sum::<i32>() as f64).sqrt();
    let magnitude2 = (vec2.iter().map(|&x| x * x).sum::<i32>() as f64).sqrt();

    // Calculate cosine similarity
    if magnitude1 == 0.0 || magnitude2 == 0.0 {
        0.0
    } else {
        dot_product as f64 / (magnitude1 * magnitude2)
    }
}

assertions:
#[test]
fn test_calculate_cosine_similarity() {
    // Test identical texts
    assert_eq!(calculate_cosine_similarity("hello world", "hello world"), 1.0);

    // Test same words, different order
    assert_eq!(calculate_cosine_similarity("hello world", "world hello"), 1.0);

    // Test some overlap
    assert!(calculate_cosine_similarity("hello world", "goodbye world") > 0.0 && 
            calculate_cosine_similarity("hello world", "goodbye world") < 1.0);

    // Test no overlap
    assert_eq!(calculate_cosine_similarity("hello world", "foo bar"), 0.0);

    // Test empty strings
    assert_eq!(calculate_cosine_similarity("", ""), 0.0);

    // Test one text is subset of another
    assert!(calculate_cosine_similarity("hello", "hello world") > 0.0 && 
            calculate_cosine_similarity("hello", "hello world") < 1.0);

    // Test case difference
    assert_eq!(calculate_cosine_similarity("rust is great", "Rust IS GREAT"), 1.0);
}

