// Function to find the maximum sum of any contiguous subarray (Kadane's algorithm)
// This is a common algorithm for solving the maximum subarray problem.
fn max_beauty_subarray(n: usize, beauty_factors: &[i32]) -> i32 {
    // Initialize variables to track the current and global maximum sums
    let mut max_current = beauty_factors[0];
    let mut max_global = beauty_factors[0];

    // Iterate through the beauty factors starting from the second element
    for i in 1..n {
        // Update the current maximum sum: either start a new subarray or continue the current one
        max_current = beauty_factors[i].max(max_current + beauty_factors[i]);

        // Update the global maximum sum if the current maximum is greater
        if max_current > max_global {
            max_global = max_current;
        }
    }

    max_global
}

assertions:
#[test]
fn test_max_beauty_subarray() {
    // Simple case with positive and negative numbers
    assert_eq!(max_beauty_subarray(3, &[1, -2, 3]), 3);
    // Case with negative numbers and a positive subarray
    assert_eq!(max_beauty_subarray(5, &[-2, -3, 4, -1, -2]), 4);
    // Single element
    assert_eq!(max_beauty_subarray(1, &[5]), 5);
    // All positive numbers
    assert_eq!(max_beauty_subarray(4, &[1, 2, 3, 4]), 10);
    // All negative numbers
    assert_eq!(max_beauty_subarray(4, &[-1, -2, -3, -4]), -1);
}

