use std::collections::HashMap;

/// Represents a library with book catalog and borrowing functionality
struct Library {
    catalog: HashMap<String, String>,          // Maps book titles to authors
    borrowed_books: HashMap<String, String>,   // Maps book titles to usernames
}

impl Library {
    /// Creates a new, empty Library
    fn new() -> Self {
        Library {
            catalog: HashMap::new(),
            borrowed_books: HashMap::new(),
        }
    }

    /// Adds a book to the library catalog if it doesn't already exist
    fn add_book(&mut self, title: &str, author: &str) {
        self.catalog.entry(title.to_string()).or_insert(author.to_string());
    }

    /// Allows a user to borrow a book if it's available
    fn borrow_book(&mut self, username: &str, title: &str) -> bool {
        if self.catalog.contains_key(title) && !self.borrowed_books.contains_key(title) {
            self.borrowed_books.insert(title.to_string(), username.to_string());
            true
        } else {
            false
        }
    }

    /// Allows a user to return a book they previously borrowed
    fn return_book(&mut self, username: &str, title: &str) -> bool {
        if let Some(borrower) = self.borrowed_books.get(title) {
            if borrower == username {
                self.borrowed_books.remove(title);
                return true;
            }
        }
        false
    }

    /// Lists all currently available books (title and author)
    fn list_available_books(&self) -> HashMap<String, String> {
        self.catalog
            .iter()
            .filter(|(title, _)| !self.borrowed_books.contains_key(*title))
            .map(|(title, author)| (title.clone(), author.clone()))
            .collect()
    }

    /// Lists all currently borrowed books (title, author, and borrower)
    fn list_borrowed_books(&self) -> HashMap<String, (String, String)> {
        self.borrowed_books
            .iter()
            .filter_map(|(title, username)| {
                self.catalog
                    .get(title)
                    .map(|author| (title.clone(), (author.clone(), username.clone())))
            })
            .collect()
    }
}

assertions:
#[test]
fn test_library_operations() {
    let mut library = Library::new();

    // Add books to the library
    library.add_book("The Rust Programming Language", "Steve Klabnik and Carol Nichols");
    library.add_book("1984", "George Orwell");
    library.add_book("To Kill a Mockingbird", "Harper Lee");
    library.add_book("The Great Gatsby", "F. Scott Fitzgerald");

    // Test initial state
    assert_eq!(library.list_available_books().len(), 4);
    assert_eq!(library.list_borrowed_books().len(), 0);

    // Test borrowing books
    assert!(library.borrow_book("user1", "1984"));
    assert!(library.borrow_book("user2", "The Rust Programming Language"));
    assert!(!library.borrow_book("user1", "Nonexistent Book"));
    assert!(!library.borrow_book("user3", "1984"));

    // Test state after borrowing
    assert_eq!(library.list_available_books().len(), 2);
    assert_eq!(library.list_borrowed_books().len(), 2);

    // Test returning books
    assert!(library.return_book("user1", "1984"));
    assert!(!library.return_book("user2", "1984"));
    assert!(!library.return_book("user3", "Nonexistent Book"));
    assert!(library.return_book("user2", "The Rust Programming Language"));

    // Test final state
    assert_eq!(library.list_available_books().len(), 4);
    assert_eq!(library.list_borrowed_books().len(), 0);
}

