use std::collections::HashMap;

/// Cleans a sentence by removing non-alphabetic characters, converting to lowercase,
/// and sorting the characters alphabetically.
fn clean_sentence(sentence: &str) -> String {
    let cleaned: String = sentence
        .chars()
        .filter(|c| c.is_alphabetic())
        .map(|c| c.to_ascii_lowercase())
        .collect();

    let mut chars: Vec<char> = cleaned.chars().collect();
    chars.sort();
    chars.into_iter().collect()
}

/// Groups sentences into anagrams by their cleaned form.
/// Returns a HashMap where keys are cleaned sentences and values are vectors of original sentences.
fn find_anagram_sentences(sentences: &[&str]) -> HashMap<String, Vec<String>> {
    let mut anagram_dict: HashMap<String, Vec<String>> = HashMap::new();

    for &sentence in sentences {
        let cleaned = clean_sentence(sentence);
        anagram_dict
            .entry(cleaned)
            .or_default()
            .push(sentence.to_string());
    }

    anagram_dict
}

assertions:
#[test]
fn test_anagram_sentences() {
    // Test case 1
    let case1 = vec!["Listen", "silent", "enlist", "hello", "world"];
    let anagrams1 = find_anagram_sentences(&case1);
    assert_eq!(
        anagrams1.get(&"eilnst".to_string()),
        Some(&vec!["Listen".to_string(), "silent".to_string(), "enlist".to_string()])
    );
    assert_eq!(anagrams1.get(&"ehllo".to_string()), Some(&vec!["hello".to_string()]));
    assert_eq!(anagrams1.get(&"dlorw".to_string()), Some(&vec!["world".to_string()]));

    // Test case 2
    let case2 = vec!["Dormitory", "Dirty room", "Clint Eastwood", "Old West action"];
    let anagrams2 = find_anagram_sentences(&case2);
    assert_eq!(
        anagrams2.get(&"dimooprrty".to_string()),
        Some(&vec!["Dormitory".to_string(), "Dirty room".to_string()])
    );
    assert_eq!(
        anagrams2.get(&"acdeilnoostw".to_string()),
        Some(&vec!["Clint Eastwood".to_string(), "Old West action".to_string()])
    );

    // Test case 3
    let case3 = vec!["The eyes", "They see", "Coffee", "Fee fco"];
    let anagrams3 = find_anagram_sentences(&case3);
    assert_eq!(
        anagrams3.get(&"eeehsty".to_string()),
        Some(&vec!["The eyes".to_string(), "They see".to_string()])
    );
    assert_eq!(
        anagrams3.get(&"ceeffo".to_string()),
        Some(&vec!["Coffee".to_string(), "Fee fco".to_string()])
    );
}

