use std::io;

// Processes a series of queries on building step costs
// n - number of buildings (unused in this implementation)
// buildings - mutable reference to a vector of vectors representing building steps and their costs
// queries - vector of query strings to process
// Returns a vector of results for query type 1 (sum operations)
fn process_queries(n: usize, buildings: &mut Vec<Vec<i32>>, queries: Vec<&str>) -> Vec<i32> {
    let mut results = Vec::new();

    for query in queries {
        let parts: Vec<&str> = query.split_whitespace().collect();
        let query_type: i32 = parts[0].parse().unwrap();
        let b: usize = parts[1].parse::<usize>().unwrap() - 1; // Convert to 0-based index

        match query_type {
            1 => {
                // Sum query: sum steps from x to y (inclusive)
                let x: usize = parts[2].parse::<usize>().unwrap() - 1; // 0-based start
                let y: usize = parts[3].parse::<usize>().unwrap(); // 1-based end (inclusive)
                let sum: i32 = buildings[b][x..y].iter().sum();
                results.push(sum);
            }
            2 => {
                // Insert query: insert new step at position p with cost c
                let p: usize = parts[2].parse::<usize>().unwrap() - 1; // 0-based position
                let c: i32 = parts[3].parse().unwrap();
                buildings[b].insert(p, c);
            }
            3 => {
                // Delete query: remove step at position p
                let p: usize = parts[2].parse::<usize>().unwrap() - 1; // 0-based position
                buildings[b].remove(p);
            }
            _ => panic!("Invalid query type"),
        }
    }

    results
}

assertions:
#[test]
fn test_process_queries() {
    let n = 3;
    let mut buildings = vec![
        vec![10, 20, 30, 40, 50],
        vec![15, 25, 35],
        vec![5, 10, 15, 20]
    ];
    let queries = vec![
        "1 1 2 4",  // Sum building 1 steps 2-4 (20+30+40)
        "2 2 2 30", // Insert cost 30 at position 2 in building 2
        "1 2 1 3",  // Sum building 2 steps 1-3 (25+30+35)
        "3 1 3",    // Remove step 3 from building 1 (40)
        "1 1 1 3",  // Sum building 1 steps 1-3 (20+30+50)
        "1 3 2 4"   // Sum building 3 steps 2-4 (10+15+20)
    ];

    let results = process_queries(n, &mut buildings, queries);

    assert_eq!(results, vec![90, 90, 100, 45]);
    assert_eq!(buildings[0], vec![10, 20, 30, 50]);
    assert_eq!(buildings[1], vec![15, 25, 30, 35]);
    assert_eq!(buildings[2], vec![5, 10, 15, 20]);
}

