fn consecutive_product_sequence(arr: &[i32]) -> Vec<i32> {
    // Replaces each element in the list (except first and last) with the product of the previous and next elements.
    // For lists with less than 3 elements, returns the input unchanged.

    let n = arr.len();
    // If the list has less than 3 elements, return a copy of it
    if n < 3 {
        return arr.to_vec();
    }

    // Create a new vector to store the result
    let mut result = arr.to_vec();

    // Iterate through elements (excluding first and last)
    for i in 1..n-1 {
        result[i] = arr[i-1] * arr[i+1];
    }

    result
}

assertions:
#[test]
fn test_consecutive_product_sequence() {
    assert_eq!(consecutive_product_sequence(&[1, 2, 3, 4]), vec![1, 3, 8, 4]);
    assert_eq!(consecutive_product_sequence(&[5, 6]), vec![5, 6]);
    assert_eq!(consecutive_product_sequence(&[10]), vec![10]);
    assert_eq!(consecutive_product_sequence(&[2, 3, 5, 7, 11]), vec![2, 10, 21, 55, 11]);
    assert_eq!(consecutive_product_sequence(&[0, 1, 0, 1, 0]), vec![0, 0, 0, 0, 0]);
    assert_eq!(consecutive_product_sequence(&[-1, -2, -3, -4]), vec![-1, 3, 8, -4]);
}

