use std::collections::HashMap;

/// Determines the hour (in 24-hour format) that contains the most events.
/// If multiple hours contain the maximum number of events, returns the earliest hour.
///
/// # Arguments
/// * `timestamps` - A slice of string slices representing timestamps in "YYYY-MM-DD HH:MM:SS" format.
///
/// # Returns
/// A tuple containing the most common hour (as a String) and the count of events in that hour.
fn most_frequent_hour(timestamps: &[&str]) -> (String, usize) {
    let mut hourly_count = HashMap::new();

    // Count occurrences of each hour
    for timestamp in timestamps {
        let hour = timestamp.split_whitespace()
                           .nth(1)
                           .and_then(|time| time.split(':').next())
                           .unwrap_or("");
        *hourly_count.entry(hour.to_string()).or_insert(0) += 1;
    }

    // Find the hour with maximum count, using the earliest hour in case of ties
    let most_common_hour = hourly_count.iter()
        .max_by_key(|&(hour, count)| (count, -(hour.parse::<i32>().unwrap_or(0))))
        .map(|(hour, count)| (hour.clone(), *count))
        .unwrap_or_else(|| (String::new(), 0));

    most_common_hour
}

assertions:
#[test]
fn test_most_frequent_hour() {
    // Test case 1: Multiple timestamps with 12 as most frequent hour
    assert_eq!(
        most_frequent_hour(&[
            "2023-01-01 12:30:45",
            "2023-01-01 12:45:00",
            "2023-01-02 13:15:30",
            "2023-01-02 12:10:00",
            "2023-01-03 12:00:00",
        ]),
        ("12".to_string(), 3)
    );

    // Test case 2: Tie between hours, should return earliest hour
    assert_eq!(
        most_frequent_hour(&[
            "2023-01-01 09:00:00",
            "2023-01-01 09:30:00",
            "2023-01-01 10:00:00",
            "2023-01-02 09:00:00",
        ]),
        ("09".to_string(), 2)
    );

    // Test case 3: All hours unique, should return earliest hour
    assert_eq!(
        most_frequent_hour(&[
            "2023-01-01 00:00:00",
            "2023-01-01 23:59:59",
            "2023-01-02 00:00:01",
        ]),
        ("00".to_string(), 2)
    );

    // Test case 4: Single timestamp
    assert_eq!(
        most_frequent_hour(&["2023-01-01 15:30:00"]),
        ("15".to_string(), 1)
    );

    // Test case 5: Empty input
    assert_eq!(
        most_frequent_hour(&[]),
        ("".to_string(), 0)
    );
}

