fn sum_except_self(lst: &[i32]) -> Vec<i32> {
    // Calculate the total sum of all elements in the list
    let total_sum: i32 = lst.iter().sum();

    // For each element in the list, subtract the element from the total sum
    lst.iter().map(|&x| total_sum - x).collect()
}

