use std::cmp::max;

/// Computes the maximum amount of gold that can be collected from a grid,
/// moving only right, right-up, or right-down from each cell.
///
/// # Arguments
/// * `grid` - A 2D vector representing the gold grid
///
/// # Returns
/// The maximum amount of gold that can be collected
fn max_gold(grid: Vec<Vec<i32>>) -> i32 {
    let n = grid.len();
    if n == 0 {
        return 0;
    }
    let m = grid[0].len();

    // Create a DP table initialized to 0
    let mut dp = vec![vec![0; m]; n];

    // Initialize first column
    for i in 0..n {
        dp[i][0] = grid[i][0];
    }

    // Fill the DP table
    for j in 1..m {
        for i in 0..n {
            let gold_from_left = dp[i][j - 1];
            let gold_from_left_up = if i > 0 { dp[i - 1][j - 1] } else { 0 };
            let gold_from_left_down = if i < n - 1 { dp[i + 1][j - 1] } else { 0 };

            dp[i][j] = grid[i][j] + max(gold_from_left, max(gold_from_left_up, gold_from_left_down));
        }
    }

    // Find the maximum in the last column
    dp.iter().map(|row| row[m - 1]).max().unwrap_or(0)
}

assertions:
#[test]
fn test_max_gold() {
    assert_eq!(
        max_gold(vec![
            vec![1, 3, 3],
            vec![2, 1, 4],
            vec![0, 6, 4]
        ]),
        12
    );
    assert_eq!(
        max_gold(vec![
            vec![1, 3, 1, 5],
            vec![2, 2, 4, 1],
            vec![5, 0, 2, 3],
            vec![0, 6, 1, 2]
        ]),
        16
    );
    assert_eq!(
        max_gold(vec![
            vec![10, 0, 0, 0],
            vec![0, 0, 0, 0],
            vec![0, 0, 0, 0]
        ]),
        10
    );
    assert_eq!(
        max_gold(vec![
            vec![1, 2],
            vec![3, 4]
        ]),
        6
    );
    assert_eq!(
        max_gold(vec![
            vec![5]
        ]),
        5
    );
}

