fn reorder_odd_even(arr: &mut Vec<i32>) -> Vec<i32> {
    // Reorders the elements of the array such that all odd integers come before
    // all even integers, maintaining the relative order among the odd and even
    // integers as in the original array.
    if arr.is_empty() {
        return arr.clone();
    }

    let mut odd_idx = 0; // pointer for odd numbers

    for i in 0..arr.len() {
        if arr[i] % 2 == 1 {
            let val = arr.remove(i);
            arr.insert(odd_idx, val);
            odd_idx += 1;
        }
    }

    arr.clone()
}

