/// Checks if a number is prime and returns its factors if it's not prime.
///
/// # Arguments
/// * `n` - The number to check for primality.
///
/// # Returns
/// A tuple containing:
/// - A boolean indicating whether the number is prime (true) or not (false)
/// - A vector of factors (empty if the number is prime)
fn is_prime(n: i32) -> (bool, Vec<i32>) {
    // Handle edge cases
    if n <= 1 {
        return (false, Vec::new());
    }
    if n == 2 {
        return (true, Vec::new());
    }

    let mut factors = Vec::new();
    let sqrt_n = (n as f64).sqrt() as i32 + 1;

    // Check for factors from 2 to sqrt(n)
    for i in 2..sqrt_n {
        if n % i == 0 {
            factors.push(i);
            let complement = n / i;
            if i != complement {
                factors.push(complement);
            }
        }
    }

    // If no factors found, it's prime
    if factors.is_empty() {
        (true, factors)
    } else {
        factors.sort();
        (false, factors)
    }
}

assertions:
