use std::collections::HashMap;

/// Creates a vector with items that appear more than once in the input vector.
/// Each item appears in the new vector as many times as the square of 
/// the number of appearances in the input vector.
fn create_duplicate_list(my_list: &[i32]) -> Vec<i32> {
    // Count occurrences of each item in the input list
    let mut counts = HashMap::new();
    for &item in my_list {
        *counts.entry(item).or_insert(0) += 1;
    }

    let mut result = Vec::new();

    // For each item that appears more than once, add it to the result
    // count^2 times
    for (&item, &count) in counts.iter() {
        if count > 1 {
            result.extend(std::iter::repeat(item).take(count * count));
        }
    }

    result
}

assertions:
