// Function to check if a given square matrix is a magic square.
// A magic square is a square matrix where the sums of each row, each column, and both main diagonals are all equal.
fn is_magic_square(matrix: &Vec<Vec<i32>>) -> bool {
    let n = matrix.len();
    if n == 0 {
        return false;
    }

    // Calculate the target sum from the first row
    let target_sum: i32 = matrix[0].iter().sum();

    // Check sums of all rows
    for row in matrix {
        if row.iter().sum::<i32>() != target_sum {
            return false;
        }
    }

    // Check sums of all columns
    for col in 0..n {
        let col_sum: i32 = matrix.iter().map(|row| row[col]).sum();
        if col_sum != target_sum {
            return false;
        }
    }

    // Check sum of the main diagonal
    let diag_sum: i32 = (0..n).map(|i| matrix[i][i]).sum();
    if diag_sum != target_sum {
        return false;
    }

    // Check sum of the anti-diagonal
    let anti_diag_sum: i32 = (0..n).map(|i| matrix[i][n - 1 - i]).sum();
    if anti_diag_sum != target_sum {
        return false;
    }

    true
}

assertions:
#[test]
fn test_is_magic_square() {
    assert_eq!(
        is_magic_square(&vec![
            vec![2, 7, 6],
            vec![9, 5, 1],
            vec![4, 3, 8],
        ]),
        true
    );
    assert_eq!(
        is_magic_square(&vec![
            vec![1, 1, 1],
            vec![1, 1, 1],
            vec![1, 1, 1],
        ]),
        true
    );
    assert_eq!(
        is_magic_square(&vec![
            vec![1, 2, 3],
            vec![4, 5, 6],
            vec![7, 8, 9],
        ]),
        false
    );
    assert_eq!(
        is_magic_square(&vec![
            vec![16, 3, 2, 13],
            vec![5, 10, 11, 8],
            vec![9, 6, 7, 12],
            vec![4, 15, 14, 1],
        ]),
        true
    );
    assert_eq!(
        is_magic_square(&vec![
            vec![1, 2],
            vec![3, 4],
        ]),
        false
    );
}

