use std::collections::{HashMap, HashSet};

/// Finds all paths from start node to end node in an unweighted graph using an iterative approach.
///
/// # Arguments
/// * `graph` - A HashMap representing an adjacency list of the graph
/// * `start` - The starting node
/// * `end` - The end node
///
/// # Returns
/// A vector of vectors, where each inner vector represents a path from start to end
fn find_all_paths_iterative(
    graph: &HashMap<String, Vec<String>>,
    start: &str,
    end: &str,
) -> Vec<Vec<String>> {
    let mut all_paths = Vec::new();
    let mut stack = vec![(start.to_string(), vec![start.to_string()])];

    while let Some((current_node, path)) = stack.pop() {
        if current_node == end {
            all_paths.push(path);
        } else {
            if let Some(neighbors) = graph.get(&current_node) {
                for neighbor in neighbors {
                    // Convert path to HashSet for efficient lookup
                    let path_set: HashSet<_> = path.iter().collect();
                    if !path_set.contains(neighbor) {
                        let mut new_path = path.clone();
                        new_path.push(neighbor.to_string());
                        stack.push((neighbor.to_string(), new_path));
                    }
                }
            }
        }
    }

    all_paths
}

assertions:
#[test]
fn test_find_all_paths_iterative() {
    // Test graph represented as an adjacency list
    let mut graph = HashMap::new();
    graph.insert(
        "A".to_string(),
        vec!["B".to_string(), "C".to_string(), "D".to_string()],
    );
    graph.insert("B".to_string(), vec!["E".to_string(), "F".to_string()]);
    graph.insert("C".to_string(), vec!["G".to_string()]);
    graph.insert("D".to_string(), vec!["H".to_string()]);
    graph.insert("E".to_string(), vec!["F".to_string()]);
    graph.insert("F".to_string(), vec!["G".to_string()]);
    graph.insert("G".to_string(), vec!["H".to_string()]);

    // Test case 1: Simple path (A -> H)
    let paths = find_all_paths_iterative(&graph, "A", "H");
    assert_eq!(paths.len(), 6); // There are 6 possible paths from A to H

    // Test case 2: Start equals end (A -> A)
    let paths = find_all_paths_iterative(&graph, "A", "A");
    assert_eq!(paths, vec![vec!["A".to_string()]]);

    // Test case 3: No path exists (A -> Z)
    let paths = find_all_paths_iterative(&graph, "A", "Z");
    assert_eq!(paths, Vec::<Vec<String>>::new());
}

