fn count_substring(main_string: &str, substring: &str) -> usize {
    // Counts the number of times a substring appears in a main string.
    // This implementation handles overlapping occurrences correctly.

    if substring.is_empty() {
        return 0;
    }

    let mut count = 0;
    let mut start = 0;

    while let Some(pos) = main_string[start..].find(substring) {
        count += 1;
        start += pos + 1;
    }

    count
}

assertions:
#[test]
fn test_count_substring() {
    assert_eq!(count_substring("banana", "ana"), 2);
    assert_eq!(count_substring("hello world", "l"), 3);
    assert_eq!(count_substring("aaaaa", "aa"), 4);
    assert_eq!(count_substring("rust is awesome", "is"), 1);
    assert_eq!(count_substring("", "test"), 0);
    assert_eq!(count_substring("abc", ""), 0);
    assert_eq!(count_substring("same", "same"), 1);
}

