use std::collections::HashSet;

/// Determines if a subset of the given list of integers sums up to the target.
/// Uses dynamic programming to solve the subset sum problem.
fn can_reach_target_sum(nums: &[i32], target: i32) -> bool {
    let mut possible_sums = HashSet::new();
    possible_sums.insert(0);

    for &num in nums {
        // Create a temporary vector to hold current sums for iteration
        let current_sums: Vec<i32> = possible_sums.iter().cloned().collect();

        for &s in &current_sums {
            let new_sum = s + num;
            if new_sum == target {
                return true;
            }
            possible_sums.insert(new_sum);
        }
    }

    possible_sums.contains(&target)
}

