use std::collections::HashMap;

/// Finds the redundant connection in the graph that can be removed to make it a tree.
///
/// This function uses the Union-Find (Disjoint Set Union) algorithm to detect cycles in the graph.
/// When adding an edge would connect two already connected nodes, that edge is redundant.
///
/// # Arguments
/// * `edges` - A vector of tuples representing the edges of the graph
///
/// # Returns
/// The redundant edge as a tuple, or None if no redundant edge exists
fn find_redundant_connection(edges: Vec<(usize, usize)>) -> Option<(usize, usize)> {
    let mut parent: Vec<usize> = (0..=edges.len()).collect();

    /// Finds the root parent of a node with path compression
    fn find(parent: &mut Vec<usize>, x: usize) -> usize {
        if parent[x] != x {
            parent[x] = find(parent, parent[x]);
        }
        parent[x]
    }

    /// Unions two sets and returns false if they were already in the same set
    fn union(parent: &mut Vec<usize>, x: usize, y: usize) -> bool {
        let root_x = find(parent, x);
        let root_y = find(parent, y);
        if root_x == root_y {
            return false;
        }
        parent[root_x] = root_y;
        true
    }

    for &(u, v) in &edges {
        if !union(&mut parent, u, v) {
            return Some((u, v));
        }
    }

    None
}

assertions:
#[test]
fn test_find_redundant_connection() {
    assert_eq!(
        find_redundant_connection(vec![(1, 2), (1, 3), (2, 3)]),
        Some((2, 3))
    );
    assert_eq!(
        find_redundant_connection(vec![(1, 2), (2, 3), (3, 4), (1, 4), (1, 5)]),
        Some((1, 4))
    );
    assert_eq!(
        find_redundant_connection(vec![(1, 2), (2, 3), (1, 3)]),
        Some((1, 3))
    );
    assert_eq!(
        find_redundant_connection(vec![(1, 2), (2, 3), (1, 3), (3, 4)]),
        Some((1, 3))
    );
}

