fn count_attack_pairs(soldier_strengths: &[i32]) -> i32 {
    // Counts the number of unique pairs (i, j) where soldier_strengths[i] < soldier_strengths[j] and i < j
    let n = soldier_strengths.len();
    let mut count = 0;

    for i in 0..n {
        for j in (i + 1)..n {
            if soldier_strengths[i] < soldier_strengths[j] {
                count += 1;
            }
        }
    }

    count
}

