fn interleave_strings(str_list: &[&str]) -> String {
    // Interleaves characters from a list of strings character by character.
    // 
    // Args:
    // str_list: A slice of string references to be interleaved.
    //
    // Returns:
    // A String with characters interleaved from the input strings.
    // If the input is empty, returns an empty string.
    if str_list.is_empty() {
        return String::new();
    }

    // Find the maximum length among all strings
    let max_len = str_list.iter().map(|s| s.len()).max().unwrap_or(0);
    let mut result = String::new();

    for i in 0..max_len {
        for s in str_list {
            // Push the character at position i if it exists
            if let Some(c) = s.chars().nth(i) {
                result.push(c);
            }
        }
    }

    result
}

assertions:
#[test]
fn test_interleave_strings() {
    assert_eq!(interleave_strings(&["abc", "123", "xyz"]), "a1xb2yc3z");
    assert_eq!(interleave_strings(&["a", "12", "xyz"]), "a1x2y");
    assert_eq!(interleave_strings(&["", "hello", "world"]), "hweolrllod");
    assert_eq!(interleave_strings(&["single"]), "single");
    assert_eq!(interleave_strings(&[]), "");
}

