// Function to evaluate a boolean expression given as a string
// The expression can contain T (true), F (false), and operators ! (NOT), & (AND), | (OR)
// The expression is first converted to postfix notation (Reverse Polish Notation) and then evaluated
fn evaluate_boolean_expression(expression: &str) -> bool {
    // Evaluates a postfix boolean expression
    fn eval_sub_expression(tokens: &[char]) -> bool {
        let mut stack: Vec<bool> = Vec::new();

        for &token in tokens {
            match token {
                'T' => stack.push(true),
                'F' => stack.push(false),
                '!' => {
                    let operand = stack.pop().unwrap();
                    stack.push(!operand);
                },
                '&' => {
                    let right = stack.pop().unwrap();
                    let left = stack.pop().unwrap();
                    stack.push(left && right);
                },
                '|' => {
                    let right = stack.pop().unwrap();
                    let left = stack.pop().unwrap();
                    stack.push(left || right);
                },
                _ => panic!("Invalid token in expression"),
            }
        }
        stack[0]
    }

    // Converts infix expression to postfix notation using Shunting-yard algorithm
    fn to_tokens(expression: &str) -> Vec<char> {
        let mut output: Vec<char> = Vec::new();
        let mut stack: Vec<char> = Vec::new();
        let precedence = |c: char| match c {
            '!' => 3,
            '&' => 2,
            '|' => 1,
            '(' => 0,
            _ => -1,
        };

        for c in expression.chars() {
            match c {
                'T' | 'F' => output.push(c),
                '(' => stack.push(c),
                ')' => {
                    while let Some(top) = stack.pop() {
                        if top == '(' {
                            break;
                        }
                        output.push(top);
                    }
                },
                '!' | '&' | '|' => {
                    while let Some(&top) = stack.last() {
                        if precedence(c) <= precedence(top) {
                            output.push(stack.pop().unwrap());
                        } else {
                            break;
                        }
                    }
                    stack.push(c);
                },
                _ => panic!("Invalid character in expression"),
            }
        }

        while let Some(op) = stack.pop() {
            output.push(op);
        }

        output
    }

    let tokens = to_tokens(expression);
    eval_sub_expression(&tokens)
}

