// Function to calculate total fruits collected in a zigzag pattern from a garden grid
// 
// Parameters:
// - n: number of rows in the garden
// - m: number of columns in the garden (not used in calculation but kept for completeness)
// - garden: 2D vector representing the garden with fruit counts
//
// Returns:
// - Total fruits collected by summing rows left-to-right for even rows and right-to-left for odd rows
fn total_fruits_in_zigzag(n: usize, m: usize, garden: &[Vec<i32>]) -> i32 {
    let mut total_fruits = 0;

    for (i, row) in garden.iter().enumerate().take(n) {
        if i % 2 == 0 {
            // Sum the row left-to-right for even indices
            total_fruits += row.iter().sum::<i32>();
        } else {
            // Sum the row right-to-left for odd indices
            total_fruits += row.iter().rev().sum::<i32>();
        }
    }

    total_fruits
}

