fn convert_temperature(temp_str: &str) -> String {
    // Converts a temperature value between Celsius and Fahrenheit.
    //
    // Parameters:
    // temp_str: The string containing the temperature value with either "C" or "F" suffix.
    //
    // Returns:
    // The converted temperature value with the appropriate suffix or "Invalid input" if the input is not valid.
    if temp_str.len() < 2 {
        return String::from("Invalid input");
    }

    // Extracting the temperature value and the suffix
    let (temp_value_str, suffix) = temp_str.split_at(temp_str.len() - 1);
    let suffix = suffix.to_uppercase();

    // Checking if the input format is valid
    let temp_value = match temp_value_str.parse::<f64>() {
        Ok(val) => val,
        Err(_) => return String::from("Invalid input"),
    };

    // Converting temperature based on the suffix
    match suffix.as_str() {
        "C" => {
            // Convert from Celsius to Fahrenheit
            let converted_temp = temp_value * 9.0 / 5.0 + 32.0;
            format!("{:.1}F", converted_temp)
        }
        "F" => {
            // Convert from Fahrenheit to Celsius
            let converted_temp = (temp_value - 32.0) * 5.0 / 9.0;
            format!("{:.1}C", converted_temp)
        }
        _ => String::from("Invalid input"),
    }
}

assertions:
#[test]
fn test_convert_temperature() {
    assert_eq!(convert_temperature("37C"), "98.6F");
    assert_eq!(convert_temperature("100F"), "37.8C");
    assert_eq!(convert_temperature("0C"), "32.0F");
    assert_eq!(convert_temperature("32F"), "0.0C");
    assert_eq!(convert_temperature("abc"), "Invalid input");
    assert_eq!(convert_temperature("123"), "Invalid input");
    assert_eq!(convert_temperature("X23F"), "Invalid input");
    assert_eq!(convert_temperature("37c"), "98.6F"); // lowercase suffix
}

