use std::collections::HashSet;

/// Counts the number of unique subarrays of length k where all elements are distinct.
///
/// # Arguments
/// * `arr` - A slice of integers representing the input array
/// * `k` - The length of subarrays to consider
///
/// # Returns
/// The count of unique subarrays of length k with all distinct elements
fn count_unique_subarrays(arr: &[i32], k: usize) -> usize {
    if k > arr.len() {
        return 0;
    }

    let mut unique_subarrays = HashSet::new();

    for i in 0..=(arr.len() - k) {
        let subarray = &arr[i..i + k];
        let unique_elements: HashSet<_> = subarray.iter().collect();

        if unique_elements.len() == k {
            unique_subarrays.insert(subarray);
        }
    }

    unique_subarrays.len()
}

assertions:
#[test]
fn test_count_unique_subarrays() {
    assert_eq!(count_unique_subarrays(&[1, 2, 3, 4, 5], 3), 3);
    assert_eq!(count_unique_subarrays(&[1, 2, 3, 1, 2], 3), 2);
    assert_eq!(count_unique_subarrays(&[1, 1, 1, 1, 1], 2), 0);
    assert_eq!(count_unique_subarrays(&[1, 2, 3, 4], 5), 0);
