use std::cmp::min;

/// Calculates the minimum cost to paint all houses with no two adjacent houses having the same color.
/// Each house has different costs for painting it red, green, or blue.
/// The costs are provided as a 2D vector where costs[i][0], costs[i][1], and costs[i][2] represent
/// the cost of painting house i red, green, and blue respectively.
fn min_cost(costs: Vec<Vec<i32>>) -> i32 {
    let n = costs.len();
    if n == 0 {
        return 0;
    }

    // Create a mutable copy of the costs to modify
    let mut dp = costs.clone();

    for i in 1..n {
        // Cost to paint current house red plus min cost of previous house being green or blue
        dp[i][0] += min(dp[i-1][1], dp[i-1][2]);

        // Cost to paint current house green plus min cost of previous house being red or blue
        dp[i][1] += min(dp[i-1][0], dp[i-1][2]);

        // Cost to paint current house blue plus min cost of previous house being red or green
        dp[i][2] += min(dp[i-1][0], dp[i-1][1]);
    }

    // The result is the minimum cost among the three options for the last house
    *dp[n-1].iter().min().unwrap()
}

