use std::collections::HashMap;

/// Calculates the frequency of each character in a string, ignoring case and spaces.
///
/// # Arguments
/// * `s` - The input string to analyze
///
/// # Returns
/// A HashMap where keys are characters (in lowercase) and values are their counts
fn character_frequency(s: &str) -> HashMap<char, usize> {
    let mut frequency = HashMap::new();

    // Iterate through each character in the string (converted to lowercase)
    for char in s.to_lowercase().chars() {
        // Skip spaces
        if char != ' ' {
            // Increment the count for this character
            *frequency.entry(char).or_insert(0) += 1;
        }
    }

    frequency
}

