use std::cmp;

fn largest_contiguous_residential_block(grid: &[Vec<char>]) -> usize {
    // Finds the largest contiguous block of residential land ('R') in the grid.
    //
    // # Arguments
    // * `grid` - A 2D vector representing the city map with characters
    //
    // # Returns
    // The size of the largest contiguous residential block as usize

    if grid.is_empty() || grid[0].is_empty() {
        return 0;
    }

    let rows = grid.len();
    let cols = grid[0].len();
    let mut visited = vec![vec![false; cols]; rows];
    let mut max_block_size = 0;

    // Nested DFS function to explore contiguous blocks
    fn dfs(r: usize, c: usize, grid: &[Vec<char>], visited: &mut Vec<Vec<bool>>) -> usize {
        if r >= grid.len() || c >= grid[0].len() || visited[r][c] || grid[r][c] != 'R' {
            return 0;
        }
        visited[r][c] = true;
        let mut size = 1;

        // Explore all four directions (with bounds checking)
        if r > 0 {
            size += dfs(r - 1, c, grid, visited);
        }
        if r < grid.len() - 1 {
            size += dfs(r + 1, c, grid, visited);
        }
        if c > 0 {
            size += dfs(r, c - 1, grid, visited);
        }
        if c < grid[0].len() - 1 {
            size += dfs(r, c + 1, grid, visited);
        }

        size
    }

    for r in 0..rows {
        for c in 0..cols {
            if grid[r][c] == 'R' && !visited[r][c] {
                let current_block_size = dfs(r, c, grid, &mut visited);
                max_block_size = cmp::max(max_block_size, current_block_size);
            }
        }
    }

    max_block_size
}

