    // Test case 1: Simple 2x2 grid with one block
    assert_eq!(
        largest_contiguous_residential_block(&vec![
            vec!['R', 'R'],
            vec!['R', 'C'],
        ]),
        3
    );

    // Test case 2: Grid with multiple separate blocks
    assert_eq!(
        largest_contiguous_residential_block(&vec![
            vec!['R', 'C', 'R'],
            vec!['C', 'R', 'C'],
            vec!['R', 'C', 'R'],
        ]),
        1
    );

    // Test case 3: Large contiguous block
    assert_eq!(
        largest_contiguous_residential_block(&vec![
            vec!['R', 'R', 'R', 'C'],
            vec!['R', 'C', 'R', 'C'],
            vec!['R', 'R', 'R', 'C'],
            vec!['C', 'C', 'C', 'C'],
        ]),
        7
    );

    // Test case 4: Empty grid
    assert_eq!(
        largest_contiguous_residential_block(&vec![]),
        0
    );

    // Test case 5: Grid with no residential areas
    assert_eq!(
        largest_contiguous_residential_block(&vec![
            vec!['C', 'C'],
            vec!['C', 'C'],
        ]),
        0
    );
}

